// ============================
// helpers agregados (foto base64)
// ============================
function fileToBase64(inputFile) {
  // agregado para foto Base64
  return new Promise((resolve) => {
    const f = inputFile?.files?.[0];
    if (!f) return resolve(null);
    const reader = new FileReader();
    reader.onload = () => resolve(reader.result); // data:image /;base64,...
    reader.onerror = () => resolve(null);
    reader.readAsDataURL(f);
  });
}

// ============================
// GESTIÓN DE HORARIOS (nuevo, integrado con tu versión)
// ============================
const listaHorarios = document.getElementById("listaHorarios");
const agregarHorarioBtn = document.getElementById("agregarHorario");
let horarios = []; // arreglo de { dia, horaInicio, horaFin }

// Agregar horario a la lista
agregarHorarioBtn?.addEventListener("click", () => {
  const dia = document.getElementById("dia").value;
  const horaInicio = document.getElementById("horaInicio").value;
  const horaFin = document.getElementById("horaFin").value;

  if (!horaInicio || !horaFin) {
    Swal.fire("Error", "Debes completar ambos horarios.", "error");
    return;
  }

  // Validación de duplicados
  const duplicado = horarios.some(
    (h) => h.dia === dia && h.horaInicio === horaInicio && h.horaFin === horaFin
  );
  if (duplicado) {
    Swal.fire(
      "Atención",
      "Ya cargaste ese horario para el mismo día.",
      "warning"
    );
    return;
  }

  horarios.push({ dia, horaInicio, horaFin });
  mostrarHorarios();
});

// Mostrar horarios en lista
function mostrarHorarios() {
  listaHorarios.innerHTML = "";
  horarios.forEach((h, i) => {
    const li = document.createElement("li");
    li.className =
      "list-group-item d-flex justify-content-between align-items-center";
    li.innerHTML = `
      ${h.dia}: ${h.horaInicio} - ${h.horaFin}
      <button class="btn btn-sm btn-danger" onclick="eliminarHorario(${i})">
        <i class="bi bi-trash"></i>
      </button>
    `;
    listaHorarios.appendChild(li);
  });
}

// Eliminar un horario
function eliminarHorario(index) {
  horarios.splice(index, 1);
  mostrarHorarios();
}

// ============================
// constantes de formulario
// ============================
const formAltaMedicos = document.getElementById("altaMedicoForm");
const inputNombre = document.getElementById("nombre");
const inputApellido = document.getElementById("apellido");
const inputEspecialidad = document.getElementById("especialidad");
const inputMatricula = document.getElementById("matricula");
const inputFechaAlta = document.getElementById("fechaAlta");
const inputObraSocial = document.getElementById("obraSocial");
const inputEmail = document.getElementById("email");
const inputTelefono = document.getElementById("telefono");
const inputValorConsulta = document.getElementById("valorConsulta");
const inputDescripcion = document.getElementById("descripcion");
const inputFoto = document.getElementById("foto");
const tablaMedicosBody = document.querySelector("#tablaMedicos tbody");

// ============================
// modal de detalle (ver médico)
// ============================
const modalEl = document.getElementById("altaMedicoModal");
const modal = modalEl ? new bootstrap.Modal(modalEl) : null;
const resumen = document.getElementById("resumenAlta");

// ============================
// bandera para validacion
// ============================
let flagIndex = null;

// ============================
// FUNCION: ACTUALIZAR TABLA
// ============================
function ActualizarTablaMedicos() {
  const medicos = JSON.parse(localStorage.getItem("medicos")) || [];
  const tbody = document.querySelector("#tablaMedicos tbody");
  tbody.innerHTML = "";

  const toStr = (v) => (v ?? "").toString().trim() || "-";
  const money = (v) =>
    v === null || v === "" || isNaN(v)
      ? "-"
      : `$${Number(v).toLocaleString("es-AR")}`;

  medicos.forEach((m, index) => {
    // Obras sociales: soporta string "OSDE, Swiss" o array ["OSDE","Swiss"]
    let obrasTxt = "-";
    if (Array.isArray(m?.obrasSociales)) {
      obrasTxt = m.obrasSociales.length ? m.obrasSociales.join(", ") : "-";
    } else if (typeof m?.obrasSociales === "string") {
      obrasTxt = m.obrasSociales.trim() || "-";
    }

    const tr = document.createElement("tr");
    tr.innerHTML = `
      <td>${toStr(m?.apellido)}</td>
      <td>${toStr(m?.nombre)}</td>
      <td>${toStr(m?.especialidad)}</td>
      <td>${toStr(m?.matricula)}</td>
      <td>${toStr(m?.fechaAlta)}</td>
      <td>${obrasTxt}</td>
      <td>${toStr(m?.telefono)}</td>
      <td>${toStr(m?.email)}</td>
      <td>${money(m?.valorConsulta)}</td>
      <td>${toStr(m?.descripcion)}</td>
      <td class="d-flex flex-column flex-lg-row gap-1">
        <button class="btn btn-sm btn-secondary btn-ver" data-index="${index}">Ver</button>
        <button class="btn btn-sm btn-primary btn-editar" data-index="${index}">Editar</button>
        <button class="btn btn-sm btn-danger btn-eliminar" data-index="${index}">Eliminar</button>
      </td>
    `;
    tbody.appendChild(tr);
  });
}

// ============================
// EVENTOS DE BOTONES DE TABLA
// ============================
tablaMedicosBody.addEventListener("click", function (event) {
  if (event.target.classList.contains("btn-ver")) {
    const index = Number(event.target.dataset.index);
    const medicos = JSON.parse(localStorage.getItem("medicos")) || [];
    const m = medicos[index];
    if (m && resumen && modal) {
      resumen.innerHTML = `
        <strong>Ficha del médico</strong><br>
        <b>Apellido:</b> ${m.apellido}<br>
        <b>Nombre:</b> ${m.nombre}<br>
        <b>Especialidad:</b> ${m.especialidad}<br>
        <b>Matrícula:</b> ${m.matricula}<br>
        <b>Fecha de alta:</b> ${m.fechaAlta}<br>
        <b>Obras sociales:</b> ${
          Array.isArray(m.obrasSociales)
            ? m.obrasSociales.join(", ")
            : m.obrasSociales
        }<br>
        <b>Teléfono:</b> ${m.telefono}<br>
        <b>Email:</b> ${m.email}<br>
        <b>Valor consulta:</b> $${m.valorConsulta}<br>
        <b>Descripción:</b> ${m.descripcion || "-"}
      `;
      modal.show();
    }
  }

  if (event.target.classList.contains("btn-editar")) {
    const index = Number(event.target.dataset.index);
    editarMedicos(index);
  }

  if (event.target.classList.contains("btn-eliminar")) {
    const index = Number(event.target.dataset.index);
    eliminarMedicos(index);
  }
});

// ============================
// FUNCION: EDITAR MÉDICOS
// ============================
function editarMedicos(index) {
  let medicos = JSON.parse(localStorage.getItem("medicos")) || [];
  const medico = medicos[index];
  if (!medico) return;

  inputNombre.value = medico.nombre || "";
  inputApellido.value = medico.apellido || "";
  inputEspecialidad.value = medico.especialidad || "";
  inputMatricula.value = medico.matricula || "";
  inputFechaAlta.value = medico.fechaAlta || "";

  const seleccionadas = Array.isArray(medico.obrasSociales)
    ? medico.obrasSociales.map((s) => String(s).trim())
    : String(medico.obrasSociales || "")
        .split(",")
        .map((s) => s.trim());

  Array.from(inputObraSocial.options).forEach((opt) => {
    opt.selected = seleccionadas.includes(opt.value);
  });

  inputEmail.value = medico.email || "";
  inputTelefono.value = medico.telefono || "";
  inputValorConsulta.value = medico.valorConsulta ?? "";
  inputDescripcion.value = medico.descripcion || "";

  flagIndex = index;
}

// ============================
// FUNCION: ELIMINAR MÉDICOS
// ============================
async function eliminarMedicos(index) {
  let medicos = JSON.parse(localStorage.getItem("medicos")) || [];
  const nombre = medicos[index]?.nombre || "este médico";

  const { isConfirmed } = await Swal.fire({
    title: "¿Eliminar médico?",
    html: `Vas a eliminar a <b>${nombre}</b>. Esta acción no se puede deshacer.`,
    icon: "warning",
    showCancelButton: true,
    confirmButtonText: "Sí, eliminar",
    cancelButtonText: "Cancelar",
    reverseButtons: true,
    focusCancel: true,
  });

  if (!isConfirmed) return;

  medicos.splice(index, 1);
  localStorage.setItem("medicos", JSON.stringify(medicos));
  ActualizarTablaMedicos();
  flagIndex = null;

  await Swal.fire({
    icon: "success",
    title: "Eliminado",
    text: "El médico fue eliminado correctamente.",
    timer: 1500,
    showConfirmButton: false,
  });
}

// ============================
// FUNCION: ALTA DE MÉDICOS
// ============================
async function altaMedicos(event) {
  event.preventDefault();

  let medicos = JSON.parse(localStorage.getItem("medicos")) || [];

  const nuevoMedico = {
    id:
      flagIndex !== null
        ? medicos[flagIndex].id
        : medicos.length
        ? Math.max(...medicos.map((m) => m.id)) + 1
        : 1,
    nombre: inputNombre.value.trim(),
    apellido: inputApellido.value.trim(),
    especialidad: inputEspecialidad.value.trim(),
    matricula: inputMatricula.value.trim(),
    fechaAlta: inputFechaAlta.value.trim(),
    obrasSociales: Array.from(inputObraSocial.selectedOptions).map(
      (opt) => opt.value
    ),
    telefono: inputTelefono.value.trim(),
    email: inputEmail.value.trim(),
    valorConsulta: Number(inputValorConsulta.value) || 0,
    descripcion: inputDescripcion.value.trim(),
    fotoBase64: await fileToBase64(inputFoto),
    horarios: horarios,
  };

  if (horarios.length === 0) {
    Swal.fire(
      "Error",
      "Debes agregar al menos un horario de atención.",
      "error"
    );
    return;
  }

  if (flagIndex !== null) {
    medicos[flagIndex] = nuevoMedico;
    flagIndex = null;
  } else {
    medicos.push(nuevoMedico);
  }

  localStorage.setItem("medicos", JSON.stringify(medicos));
  ActualizarTablaMedicos();
  formAltaMedicos.reset();

  await Swal.fire({
    icon: "success",
    title: "Médico guardado",
    text: "Los datos fueron registrados correctamente.",
    timer: 1500,
    showConfirmButton: false,
  });
}

// ============================
// INICIO DE PÁGINA
// ============================
document.addEventListener("DOMContentLoaded", () => {
  ActualizarTablaMedicos();
});

formAltaMedicos.addEventListener("submit", altaMedicos);
